import Categories from "../components/Categories";
import style from './style.css'
import axios from "axios";
import { useEffect, useState } from "react";
import { RiMenuLine } from "react-icons/ri";
import { Link } from "react-router-dom";
import LandingPage from "../components/LandingPage";


const Main = () => {
    const [categories, setCategories] = useState([]);
    const [currCat, setCurrCat] = useState("");
    const [catMeals, setCatMeals] = useState([]);
    const [ready, setReady] = useState(false);
    const [areas, setAreas] = useState([]);



    useEffect(() => {
        axios.get('https://www.themealdb.com/api/json/v1/1/categories.php')
            .then(res=> { 
                setCategories(res.data.categories)
            })
            .catch(err => console.error(err))
    }, []);

    useEffect(() => {
        axios.get('https://www.themealdb.com/api/json/v1/1/list.php?a=list')
            .then(res=> { 
                setAreas(res.data.meals)
            })
            .catch(err => console.error(err))
    }, []);


    const searchResult = (e, value) => {
        e.preventDefault();
        axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?i=${value}`)
            .then(res=> {
                console.log(res.data)
                // setCurrCat(value)
                // setCatMeals(res.data.meals)
                // setReady(true);
            })
            .catch(err=>console.error(err))
    }



    return(
        <div>
            <div className="centered header flex sp-btw">
                <div>

                </div>
                <div>
                    <h2 className="header-link centered">QUICK & EASY RECIPES</h2>
                </div>
                <div className="padding-right-10">
                    <form onSubmit={searchResult}>
                        <input type="text" placeholder="Search by Ingredient" id="search" value={currCat} onChange={(e)=>setCurrCat(e.target.value)}/>
                        <input type="submit" value="Search"/>
                    </form>
                </div>
            </div>
            <div>
                <LandingPage categories={categories} areas={areas}/>
            </div>
            {/* <div>
                {loaded && <Categories categories={categories} ready={ready} catMeals={catMeals} currCat={currCat}/>}
            </div> */}
        </div>
    )
}

export default Main;
