    return(
        <div>
            <div className="flex sp-btw header">
                <div className="Sidebar">
                    <div className="sidebar-header">
                        <RiMenuLine className="sidebar-icon"/>
                        <h1 className="sidebar-logo">LOGO</h1>
                    </div>
                    {categories.map((cat, i)=>
                    <div key={i} className="sidebar-items">
                        <div className="item">
                            <img src={cat.strCategoryThumb} id="menupic" alt="category" className="sidebar-icon"/>
                            <span className="sidebar-text">{cat.strCategory}</span>
                        </div>
                    </div>)}
                </div>
                <div>
                    <h2 className="header-link">QUICK & EASY RECIPES</h2>
                </div>
                <div className="center">
                </div>
            </div>
            <div>
                {loaded && <Categories categories={categories}/>}
            </div>
        </div>
    )
}

export default Main;
