import { useEffect, useState } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";
import style from '../views/style.css';
import { RiMenuLine } from "react-icons/ri";


const Categories = (props) => {
    const [catMeals, setCatMeals] = useState([]);
    const [ready, setReady] = useState(false);
    const [meal, setMeal] = useState([]);
    const [finish, setFinish] = useState(false);
    const [currIngred, setCurrIngred] = useState([]);
    const [currAmt, setCurrAmt] = useState([]);
    const [currCat, setCurrCat] = useState("");


    const currentCat = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${value}`)
            .then(res=> {
                setCurrCat(value)
                setCatMeals(res.data.meals)
                setReady(true);
            })
            .catch(err=>console.error(err))
    }

    const currMeal = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${value}`)
            .then(res=> {
                setMeal(res.data.meals[0])
                setFinish(true);
                let ingredients = []
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let single = "strIngredient" + i;
                        if(key === single){
                            if(value !== '' && value !== null){
                                ingredients.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrIngred(ingredients);
                let measure = [];
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let singleVal = "strMeasure" + i;
                        if(key === singleVal){
                            if(value !== '' && value !== null){
                                measure.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrAmt(measure);
            })
            .catch(err=>console.error(err))
    }

        /* Set the width of the sidebar to 250px and the left margin of the page content to 250px */
    const openNav = () => {
        document.getElementById("mySidebar").style.width = "250px";
        document.getElementById("main").style.marginLeft = "250px";
    }

    /* Set the width of the sidebar to 0 and the left margin of the page content to 0 */
    const closeNav = () => {
        document.getElementById("mySidebar").style.width = "0";
        document.getElementById("main").style.marginLeft = "0";
    }


    return (
        <div className="colored">
                <div className="Sidebar" id="mySidebar">
                    <div className="sidebar-header">
                        <RiMenuLine className="sidebar-icon closebtn" onclick={closeNav}/>
                        <h1 className="sidebar-logo">ALL CATEGORIES</h1>
                    </div>
                    {props.categories.map((cat, i)=>
                    <div key={i} className="sidebar-items">
                        <Link className="header-link" onClick={(e)=>{currentCat(cat.strCategory)}}>
                        <div className="item">
                            <img src={cat.strCategoryThumb} id="menupic" alt="category" className="sidebar-icon"/>
                            <span className="sidebar-text">{cat.strCategory}</span>
                        </div>
                        </Link>
                    </div>)}
                </div>
            <div className="flex sp-btw header" id="main">
                <div>
                    <RiMenuLine onclick={openNav} className="sidebar-icon openbtn"/>
                </div>
                <div>
                    <h2 className="header-link">QUICK & EASY RECIPES</h2>
                </div>
                <div className="center">
                </div>
            </div>
        <div>
            <div className="flex single sp-btw header padding-10">
                {props.categories.map((cat, i)=>
                <div key={i} className="padding-10 one-cat">
                    <Link className="header-link" onClick={(e)=>{currentCat(cat.strCategory)}}>
                        <div>
                            <img src={cat.strCategoryThumb} id="menupic" alt="category"/>
                        </div>
                        <div className="center cool-link">
                                {cat.strCategory}
                        </div>
                    </Link>
                </div>)}
            </div>
        </div>
        <div className="flex centered background padding-bottom-20">
            <div className="centered">
                <h2 className="center">{currCat}</h2>
                <div className="recipes">
                    {ready && catMeals ?
                    catMeals.map((rec, i) => {
                        return <div key={i} className="flex scroll">
                            <div>
                                <img src={rec.strMealThumb} id="subpic"/>
                            </div>
                            <div className="marg-left-20 marg-right-20">
                                <Link className="rec-link" onClick={(e)=>{currMeal(rec.idMeal)}}>
                                    {rec.strMeal}
                                </Link>
                            </div>
                    </div> }) : <></>
                    }
                </div>
            </div>
            <div className="centered">
                {finish && meal ? <div className="recipe padding-10 marg-top-20">
                <h2 className="center">{meal.strMeal}</h2>
                <div className="flex">
                    <div>
                        <img src={meal.strMealThumb} id="recipepic"/>
                    </div>
                    <div className="marg-left-20">
                        <p>Area: {meal.strArea}</p>
                        <p>Category: {meal.strCategory}</p>
                        Ingredients:
                        <div className="flex sp-btw">
                            <div>
                                {currIngred.map((ingred, i)=>
                                <div key={i}>
                                    <li>{ingred}</li>
                                </div>)}
                            </div>
                            <div>
                                {currAmt.map((amt, i)=>
                                <div key={i} className="marg-left-5">
                                    {amt}
                                </div>)}
                            </div>
                        </div>
                    </div>
                </div>
                <div>
                    <p>Instructions:</p>
                    <p>{meal.strInstructions}</p>
                </div>
                </div>: <></> }
        </div>
        </div></div>
    )
}

export default Categories;
