import {useEffect, useState} from 'react'
import axios from 'axios'
import style from './style.css'

const Food = () => {
    const [creatures, setCreatures] = useState([]);
    const [loaded, setLoaded] = useState(false);

    useEffect(()=>{
        axios.get('https://botw-compendium.herokuapp.com/api/v2/all')
            .then(res => {
                setCreatures(res.data.data.creatures.food)
                setLoaded(true);
            })
            .catch(err=>console.error(err))
    }, []);

    return(
        <div>
            {loaded && creatures.map((item, i) => 
                <div key={i} className='flex marg-left-20'>
                    <div>
                        <p><img src={item.image} alt="image" id="icon"/></p>
                    </div>
                    <div className="marg-left-20">
                        <h2>{item.name}</h2>
                        <p>{item.description}</p>
                        <p>Cooking Effect: {item.cooking_effect}</p>
                        <p> Common Locations: {item.common_locations}</p>
                        <p> Hearts Recovered: {item.hearts_recovered}</p>
                    <hr />
                    </div>
                </div>
            )}
        </div>
    )
}

export default Food;
