import { useEffect, useState } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";
import style from '../views/style.css';
import { RiMenuLine } from "react-icons/ri";


const Categories = (props) => {
    const [catMeals, setCatMeals] = useState([]);
    const [ready, setReady] = useState(false);
    const [meal, setMeal] = useState([]);
    const [finish, setFinish] = useState(false);
    const [currIngred, setCurrIngred] = useState([]);
    const [currAmt, setCurrAmt] = useState([]);
    const [currCat, setCurrCat] = useState("");
    const [sidebarOpen, setSideBarOpen] = useState(false);


    const currentCat = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${value}`)
            .then(res=> {
                setCurrCat(value)
                setCatMeals(res.data.meals)
                setReady(true);
            })
            .catch(err=>console.error(err))
    }

    const currMeal = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${value}`)
            .then(res=> {
                setMeal(res.data.meals[0])
                setFinish(true);
                let ingredients = []
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let single = "strIngredient" + i;
                        if(key === single){
                            if(value !== '' && value !== null){
                                ingredients.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrIngred(ingredients);
                let measure = [];
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let singleVal = "strMeasure" + i;
                        if(key === singleVal){
                            if(value !== '' && value !== null){
                                measure.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrAmt(measure);
            })
            .catch(err=>console.error(err))
    }

    const handleViewSidebar = () => {
        console.log(sidebarOpen)
        if(sidebarOpen == false){
            setSideBarOpen(true);
        }
        if(sidebarOpen == true) {
            setSideBarOpen(false);
        }
    };

    return (
        <div className="colored">
            {sidebarOpen ?
            <div className="Sidebar">
                <div className="sidebar-header">
                    <RiMenuLine className="sidebar-icon sidebar-burger" onClick={handleViewSidebar}/>
                    <h1 className="sidebar-logo">ALL CATEGORIES</h1>
                </div>
                {props.categories.map((cat, i)=>
                <div key={i} className="sidebar-items">
                    <Link className="header-link" onClick={(e)=>{currentCat(cat.strCategory)}}>
                    <div className="item">
                        <img src={cat.strCategoryThumb} id="menupic" alt="category" className="sidebar-icon"/>
                        <span className="sidebar-text">{cat.strCategory}</span>
                    </div>
                    </Link>
                </div>)}
            </div> : <></>
            }
            <div className="flex sp-btw header">
                <div className="burger marg-left-20">
                    <RiMenuLine onClick={handleViewSidebar} className="sidebar-icon" id="RiMenuLine"/>
                </div>
                <div>
                    <h2 className="header-link">QUICK & EASY RECIPES</h2>
                </div>
                <div>
                </div>
            </div>
            <div className="centered background total-padding">
                <div className="centered">
                    <h2 className="center cat-title">{currCat.toUpperCase()}</h2>
                    <div className="flex recipes">
                        {ready && catMeals ?
                        catMeals.map((rec, i) => {
                            return <div key={i} className="scroll">
                                <div className="center">
                                    <img src={rec.strMealThumb} id="subpic"/>
                                </div>
                                <div className="recipe-title center">
                                    <Link className="rec-link" id="rec-title" onClick={(e)=>{currMeal(rec.idMeal)}}>
                                        {rec.strMeal.toUpperCase()}
                                    </Link>
                                </div>
                        </div> }) : <></>
                        }
                    </div>
                </div>
                <br />
                <hr />
                <br />
                <div className="centered">
                    {finish && meal ? <div className="recipe centered padding-10">
                    <h2 className="center marg-top-0 caps">{meal.strMeal.toUpperCase()}</h2>
                    <div className="flex">
                        <div className="centered">
                            <img src={meal.strMealThumb} id="recipepic"/>
                        </div>
                        <div className="centered body">
                            <p className="marg-top-0">Area: {meal.strArea}</p>
                            <p>Category: {meal.strCategory}</p>
                            Ingredients:
                            <div className="flex">
                                <div>
                                    {currIngred.map((ingred, i)=>
                                    <div key={i}>
                                        <li>{ingred}</li>
                                    </div>)}
                                </div>
                                <div>
                                    {currAmt.map((amt, i)=>
                                    <div key={i} className="marg-left-10">
                                        {amt}
                                    </div>)}
                                </div>
                            </div>
                        </div>
                    <div className="body padding">
                        <p>Instructions:</p>
                        <p>{meal.strInstructions}</p>
                    </div>
                    </div>
                    </div>: <></> }
                </div>
            </div>
            <br />
            <br />
            <br />
        </div>
    )
}

export default Categories;
