import style from '../views/style.css'
import axios from 'axios'
import { useState } from 'react';
import { Link } from 'react-router-dom';

const LandingPage = (props) => {
    const [currCat, setCurrCat] = useState("");
    const [currArea, setCurrArea] = useState("");
    const [catMeals, setCatMeals] = useState([]);
    const [ready, setReady] = useState(false);
    const [meal, setMeal] = useState([]);
    const [finish, setFinish] = useState(false);
    const [currIngred, setCurrIngred] = useState([]);
    const [currAmt, setCurrAmt] = useState([]);
    const [sidebarOpen, setSideBarOpen] = useState(false);

    
    
    const currentCat = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${value}`)
            .then(res=> {
                setCurrCat(value)
                setCatMeals(res.data.meals)
                setReady(true);
                setCurrArea("")
            })
            .catch(err=>console.error(err))
    }

    const currentArea = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?a=${value}`)
            .then(res=> {
                setCurrArea(value)
                setCatMeals(res.data.meals)
                setReady(true);
                setCurrCat("")
            })
            .catch(err=>console.error(err))
    }

    const currMeal = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${value}`)
            .then(res=> {
                setMeal(res.data.meals[0])
                setFinish(true);
                setSideBarOpen(true);
                let ingredients = []
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let single = "strIngredient" + i;
                        if(key === single){
                            if(value !== '' && value !== null){
                                ingredients.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrIngred(ingredients);
                let measure = [];
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let singleVal = "strMeasure" + i;
                        if(key === singleVal){
                            if(value !== '' && value !== null){
                                measure.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrAmt(measure);
            })
            .catch(err=>console.error(err))
    }


    const randMeal = () => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/random.php`)
            .then(res=> {
                console.log(res)
                setMeal(res.data.meals[0])
                setFinish(true);
                setSideBarOpen(true);
                let ingredients = []
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let single = "strIngredient" + i;
                        if(key === single){
                            if(value !== '' && value !== null){
                                ingredients.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrIngred(ingredients);
                let measure = [];
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let singleVal = "strMeasure" + i;
                        if(key === singleVal){
                            if(value !== '' && value !== null){
                                measure.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrAmt(measure);
            })
            .catch(err=>console.error(err))
    }

    const handleViewSidebar = () => {
        console.log(sidebarOpen)
        if(sidebarOpen === false){
            setSideBarOpen(true);
        }
        if(sidebarOpen === true) {
            setSideBarOpen(false);
        }
    };

    return (
        <>
            {meal && finish && sidebarOpen ?
            <div className="Sidebar body">
                    <div>
                        <button className="sidebar-icon sidebar-burger" onClick={handleViewSidebar}>X</button>
                    </div>
                    <div className="sidebar-header centered body">
                        <h1 className="sidebar-logo center">{meal.strMeal.toUpperCase()}</h1>
                    </div>
                <div className="sidebar-items centered body">
                    <img src={meal.strMealThumb} id="menupic" alt="category" className="sidebar-icon"/>
                </div>
                <br/>
                <div className='marg-left-20'>
                    INGREDIENTS:
                </div>
                <div className='ingred-list flex marg-left-10 padding-left-10'>
                {currIngred.map((ingred, i)=>
                    <div key={i}>
                        <li className="line-spc marg-right-10">{ingred}</li>
                    </div>)}
                </div>
                <div className="padding-20 line-spc">
                    <p>INSTRUCTIONS: <br />
                    <span className='text'>{meal.strInstructions}</span></p>
                </div>
                <Link to="" className='centered full-link'>VIEW FULL RECIPE HERE</Link>
            </div> : <></> }
        <div className='colored'>
            <div className='page-width centered frosty'>
                <div className='just-cent padding-top-10 body marg-bottom-20'>
                    <div className='padding flex'>
                        <div>
                            <h3 className='land-box-title'>CHOOSE BY CATEGORY</h3>
                        </div>
                        <div class="dropdown">
                            <button class="dropbtn body">CATEGORIES</button>
                            <div class="dropdown-content">
                                {props.categories.map((cat, i)=>
                                <span key={i}>
                                    <Link onClick={(e)=>{currentCat(cat.strCategory)}}>{cat.strCategory.toUpperCase()}</Link>
                                </span>
                                )}
                            </div>
                        </div>
                    </div>
                    <div className='marg-right-20 padding land-box flex'>
                        <h3 className='land-box-title'>CHOOSE BY AREA</h3>
                        <div class="dropdown">
                            <button class="dropbtn body">AREAS</button>
                            <div class="dropdown-content">
                                {props.areas.map((cat, i)=>
                                <span key={i}>
                                    <Link onClick={(e)=>{currentArea(cat.strArea)}}>{cat.strArea.toUpperCase()}</Link>
                                </span>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
                <div className='marg-left-20 padding rand-box centered body center'>
                    <h3 className='land-box-title center'>LET US PICK A MEAL FOR YOU!</h3>
                    <button className='body dropbtn' id='surprise' onClick={randMeal}>SURPRISE ME!</button>
                </div>
                <div className="centered single padding">
                    {currCat ?
                    <h2 className="center cat-title padding-bottom-10 padding-top-10">{currCat.toUpperCase()}</h2>
                    : <></>}
                    {currArea ?
                    <h2 className="center cat-title padding-bottom-10 padding-top-10">{currArea.toUpperCase()}</h2>
                    : <></>}
                    <div className="flex recipes centered frosty">
                        {ready && catMeals ?
                            catMeals.map((rec, i) => {
                            return <div key={i} className="scroll">
                                <div className="center">
                                    <img src={rec.strMealThumb} id="subpic"/>
                                </div>
                                <div className="recipe-title center">
                                    <Link className="rec-link" id="rec-title" onClick={(e)=>{currMeal(rec.idMeal)}}>
                                        {rec.strMeal.toUpperCase()}
                                    </Link>
                                </div>
                            </div> }) : <></>
                        }
                    </div>
                </div>
            <br />
            <br />
            <br />
            </div>
        </div>
        </>
    )
}

export default LandingPage;
