import { useEffect, useState } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";
import style from '../views/style.css';


const Categories = (props) => {
    const [currCat, setCurrCat] = useState([]);
    const [ready, setReady] = useState(false);
    const [meal, setMeal] = useState([]);
    const [finish, setFinish] = useState(false);
    const [currIngred, setCurrIngred] = useState([]);
    const [currAmt, setCurrAmt] = useState([]);


    const currentCat = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${value}`)
            .then(res=> {
                setCurrCat(res.data.meals)
                setReady(true);
            })
            .catch(err=>console.error(err))
    }

    const currMeal = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${value}`)
            .then(res=> {
                setMeal(res.data.meals[0])
                setFinish(true);
                let ingredients = []
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let single = "strIngredient" + i;
                        if(key === single){
                            if(value !== ''){
                                ingredients.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrIngred(ingredients);
                let measure = [];
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let singleVal = "strMeasure" + i;
                        if(key === singleVal){
                            if(value !== ''){
                                measure.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrAmt(measure);
            })
            .catch(err=>console.error(err))
    }


    return (
        <div className="flex">
            <div className=" marg-left-20 single marg-top-20">
                {props.categories.map((cat, i)=>
                <div key={i}>
                        <div className="flex">
                            <div>
                                <img src={cat.strCategoryThumb} id="menupic" alt="category"/>
                            </div>
                            <div>
                                <Link onClick={(e)=>{currentCat(cat.strCategory)}}>
                                <p>{cat.strCategory}</p>
                                </Link>
                            </div>
                        </div>
                </div>)}
            </div>
            <div className="marg-left-20 single marg-top-20">
                <div>
                    {ready && currCat ?
                    currCat.map((rec, i) => {
                        return <div key={i} className="flex">
                            <div>
                                <img src={rec.strMealThumb} id="menupic"/>
                            </div>
                            <div className="marg-left-20">
                                <Link onClick={(e)=>{currMeal(rec.idMeal)}}>
                                    {rec.strMeal}
                                </Link>
                            </div>
                    </div> }) : <></>
                    }
                </div>
            </div>
            <div className="marg-left-20 marg-top-20">
                {finish && meal ? <div className="recipe">
                <h2 className="center">{meal.strMeal}</h2>
                <div className="flex">
                    <div>
                        <img src={meal.strMealThumb} id="recipepic"/>
                    </div>
                    <div className="marg-left-20">
                        <p>Area: {meal.strArea}</p>
                        <p>Category: {meal.strCategory}</p>
                        <p>Ingredients:</p>
                        <div className="flex">
                            <div>
                                {currIngred.map((ingred, i)=>
                                <div key={i}>
                                    <li>{ingred}</li>
                                </div>)}
                            </div>
                            <div>
                                {currAmt.map((amt, i)=>
                                <div key={i}>
                                    {amt}
                                </div>)}
                            </div>
                        </div>
                    </div>
                </div>
                <div>
                    <h3>Instructions:</h3>
                    <p>{meal.strInstructions}</p>
                </div>
                </div>: <></> }
        </div>
        </div>
    )
}

export default Categories;
