import Categories from "../components/Categories";
import style from './style.css'
import CategoryList from "../components/CategoryList";
import axios from "axios";
import { useEffect, useState } from "react";
import {RiMenuLine, RiLayoutGridFill, RiChat4Fill, RiTTeamFill, RiPieChart2Fill, RiTeamFill, RiTaskFill} from 'react-icons/ri';

const Main = () => {
    const [categories, setCategories] = useState([]);
    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
        axios.get('https://www.themealdb.com/api/json/v1/1/categories.php')
            .then(res=> { 
                setCategories(res.data.categories)
                setLoaded(true);
            })
            .catch(err => console.error(err))
    }, []);

    

    return(
        <div>
            <div className="flex sp-btw header">
                <div className="Sidebar">
                    <div className="sidebar-header">
                        <RiMenuLine className="sidebar-icon"/>
                        <h1 className="sidebar-logo">LOGO</h1>
                    </div>
                    <div className="sidebar-items">
                        <div className="item">
                            <RiLayoutGridFill className="sidebar-icon" />
                            <span className="sidebar-text">Dashboard</span>
                        </div>
                        <div className="item">
                            <RiChat4Fill className="sidebar-icon" />
                            <span className="sidebar-text">Chat</span>
                        </div>
                        <div className="item">
                            <RiTeamFill className="sidebar-icon" />
                            <span className="sidebar-text">Teams</span>
                        </div>
                        <div className="item">
                            <RiTaskFill className="sidebar-icon" />
                            <span className="sidebar-text">Tasks</span>
                        </div>
                        <div className="item">
                            <RiPieChart2Fill className="sidebar-icon" />
                            <span className="sidebar-text">Analytics</span>
                        </div>
                    </div>
                </div>
                <div>
                    <h2 className="header-link">QUICK & EASY RECIPES</h2>
                </div>
                <div className="center">
                </div>
            </div>
            <div>
                {loaded && <Categories categories={categories}/>}
            </div>
        </div>
    )
}

export default Main;
