import Categories from "../components/Categories";
import style from './style.css'
import axios from "axios";
import { useEffect, useState } from "react";


const Main = () => {
    const [categories, setCategories] = useState([]);
    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
        axios.get('https://www.themealdb.com/api/json/v1/1/categories.php')
            .then(res=> { 
                setCategories(res.data.categories)
                setLoaded(true);
            })
            .catch(err => console.error(err))
    }, []);


    return(
        <div>
            {sidebarOpen ?
            <div className="Sidebar">
                <div className="sidebar-header">
                    <RiMenuLine className="sidebar-icon sidebar-burger" onClick={handleViewSidebar}/>
                    <h1 className="sidebar-logo">ALL CATEGORIES</h1>
                </div>
                {props.categories.map((cat, i)=>
                <div key={i} className="sidebar-items">
                    <Link className="header-link" onClick={(e)=>{currentCat(cat.strCategory)}}>
                    <div className="item">
                        <img src={cat.strCategoryThumb} id="menupic" alt="category" className="sidebar-icon"/>
                        <span className="sidebar-text">{cat.strCategory}</span>
                    </div>
                    </Link>
                </div>)}
            </div> : <></>
            }
            <div>
                {loaded && <Categories categories={categories}/>}
            </div>
        </div>
    )
}

export default Main;
