import Categories from "./Categories";
import axios from "axios";
import { useState } from "react";

const Meals = () => {
    const cat = Categories.currCat;
    const [meal, setMeal] = useState([]);
    const [currIngred, setCurrIngred] = useState([]);
    const [currAmt, setCurrAmt] = useState([]);

    const currMeal = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${value}`)
            .then(res=> {
                setMeal(res.data.meals[0])
                console.log(meal)
                let ingredients = []
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let single = "strIngredient" + i;
                        if(key === single){
                            if(value !== ''){
                                ingredients.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrIngred(ingredients);
                let measure = [];
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let singleVal = "strMeasure" + i;
                        if(key === singleVal){
                            if(value !== ''){
                                measure.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrAmt(measure);
            })
            .catch(err=>console.error(err))
    }

    return (
        <div className="flex">
        <div className="marg-left-20 single marg-top-20">
                <div>
                    { ready && currCat ?
                    currCat.map((rec, i) => {
                        return <div key={i} className="flex">
                            <div>
                                <img src={rec.strMealThumb} id="menupic"/>
                            </div>
                            <div className="marg-left-20">
                                <Link onClick={(e)=>{currMeal(rec.idMeal)}}>
                                    {rec.strMeal}
                                </Link>
                            </div>
                    </div> }) : <></>
                    }
                </div>
            </div>
            <div className="recipe marg-left-20">
                {meal ? <>
                <h2>{meal.strMeal}</h2>
                <div className="flex">
                    <div>
                        <img src={meal.strMealThumb} id="recipepic"/>
                    </div>
                    <div className="marg-left-20">
                        <p>Area: {meal.strArea}</p>
                        <p>Category: {meal.strCategory}</p>
                        <p>Ingredients:</p>
                        <div className="flex">
                            <div>
                                {currIngred.map((ingred, i)=>
                                <div key={i}>
                                    <li>{ingred}</li>
                                </div>)}
                            </div>
                            <div>
                                {currAmt.map((amt, i)=>
                                <div key={i}>
                                    {amt}
                                </div>)}
                            </div>
                        </div>
                    </div>
                </div>
                <div>
                    <p>Instructions: {meal.strInstructions}</p>
                </div>
                </>: <></> }
            </div>
        </div>
    )

}

export default Meals;
