import Categories from "../components/Categories";
import style from './style.css'
import axios from "axios";
import { useEffect, useState } from "react";
import { RiMenuLine } from "react-icons/ri";
import { Link } from "react-router-dom";
import LandingPage from "../components/LandingPage";


const Main = () => {
    const [categories, setCategories] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [sidebarOpen, setSideBarOpen] = useState(false);
    const [currCat, setCurrCat] = useState("");
    const [catMeals, setCatMeals] = useState([]);
    const [ready, setReady] = useState(false);
    const [areas, setAreas] = useState([]);
    const [loadedToo, setLoadedToo] = useState(false);



    useEffect(() => {
        axios.get('https://www.themealdb.com/api/json/v1/1/categories.php')
            .then(res=> { 
                setCategories(res.data.categories)
                setLoaded(true);
            })
            .catch(err => console.error(err))
    }, []);

    useEffect(() => {
        axios.get('https://www.themealdb.com/api/json/v1/1/list.php?a=list')
            .then(res=> { 
                setAreas(res.data.meals)
                setLoadedToo(true);
            })
            .catch(err => console.error(err))
    }, []);


    const currentCat = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${value}`)
            .then(res=> {
                setCurrCat(value)
                setCatMeals(res.data.meals)
                setReady(true);
            })
            .catch(err=>console.error(err))
    }

    const handleViewSidebar = () => {
        console.log(sidebarOpen)
        if(sidebarOpen == false){
            setSideBarOpen(true);
        }
        if(sidebarOpen == true) {
            setSideBarOpen(false);
        }
    };




    return(
        <div>
            {finish && meal ? 
            <div className="Sidebar">
                <div className="sidebar-header">
                    <RiMenuLine className="sidebar-icon sidebar-burger" onClick={handleViewSidebar}/>
                    <h1 className="sidebar-logo">{meal.strMeal.toUpperCase()}</h1>
                </div>
                <div className="sidebar-items">
                    <div className="item">
                        <img src={meal.strMealThumb} id="menupic" alt="category" className="sidebar-icon"/>
                        <span className="sidebar-text"></span>
                    </div>
                </div>
            </div> : <></> }
            <div className="flex sp-btw header">
                <div className="burger marg-left-20">
                    <RiMenuLine onClick={handleViewSidebar} className="sidebar-icon" id="RiMenuLine"/>
                </div>
                <div>
                    <h2 className="header-link center">QUICK & EASY RECIPES</h2>
                </div>
                <div>
                </div>
            </div>
            <div>
                <LandingPage categories={categories} areas={areas}/>
            </div>
            {/* <div>
                {loaded && <Categories categories={categories} ready={ready} catMeals={catMeals} currCat={currCat}/>}
            </div> */}
        </div>
    )
}

export default Main;
