import AllInfo from "../components/AllInfo";
import {useEffect, useState} from 'react'
import axios from 'axios'
import style from './style.css'

const Main = () => {
    const [creatures, setCreatures] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [foodItems, setFoodItems] = useState([])

    useEffect(()=>{
        axios.get('https://botw-compendium.herokuapp.com/api/v2/all')
            .then(res => {
                setCreatures(res.data.data.creatures.food)
                console.log((res.data.data.creatures))
                let newArr = [];
                for(let i=0; i<creatures.length; i++){
                    newArr.push(creatures[i]);
                }
                setFoodItems(newArr);
            })
            .catch(err=>console.error(err))
    }, []);

    return(
        <div>
            {foodItems.map((item, i) => 
                <div key={i} className='flex'>
                    <div>
                        <p><img src={item.image} alt="image" id="icon"/></p>
                    </div>
                    <div>
                        <h2>{item.name}</h2>
                        <p>{item.description}</p>
                        <p>Cooking Effect: {item.cooking_effect}</p>
                        <p> Common Locations: {item.common_locations}</p>
                        <p> Hearts Recovered: {item.hearts_recovered}</p>
                        <hr />
                    </div>
                </div>
            )}
        </div>
    )
}

export default Main;
