import { useEffect, useState } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";
import style from '../views/style.css';


const Categories = () => {
    const [categories, setCategories] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [currCat, setCurrCat] = useState([]);
    const [ready, setReady] = useState(false);
    const [meal, setMeal] = useState([]);
    const [currIngred, setCurrIngred] = useState([]);

    useEffect(() => {
        axios.get('https://www.themealdb.com/api/json/v1/1/categories.php')
            .then(res=> { 
                setCategories(res.data.categories)
                setLoaded(true);
            })
            .catch(err => console.error(err))
    }, []);

    const currentCat = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${value}`)
            .then(res=> {
                setCurrCat(res.data.meals)
                setReady(true);
            })
            .catch(err=>console.error(err))
    }

    const currMeal = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${value}`)
            .then(res=> {
                setMeal(res.data.meals[0])
                let ingredients = []
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let single = "strIngredient" + i;
                        if(key === single){
                            ingredients.push(`${value}`)
                        }
                    }
                }
                setCurrIngred(ingredients);
                console.log(ingredients)
            })
            .catch(err=>console.error(err))
    }

    return (
        <div className="flex">
            <div>
                {loaded && categories.map((cat, i)=>
                <div key={i}>
                        <div className="flex">
                            <div>
                                <img src={cat.strCategoryThumb} id="menupic" alt="category"/>
                            </div>
                            <div>
                                <Link onClick={(e)=>{currentCat(cat.strCategory)}}>
                                <p>{cat.strCategory}</p>
                                </Link>
                            </div>
                        </div>
                </div>)}
            </div>
            <div className="marg-left-20">
                <div>
                    { ready && currCat ?
                    currCat.map((rec, i) => {
                        return <div key={i} className="flex">
                            <div>
                                <img src={rec.strMealThumb} id="menupic"/>
                            </div>
                            <div className="marg-left-20">
                                <Link onClick={(e)=>{currMeal(rec.idMeal)}}>
                                    {rec.strMeal}
                                </Link>
                            </div>
                    </div> }) : <></>
                    }
                </div>
            </div>
            <div>
                <h2>{meal.strMeal}</h2>
                <h4>Area: {meal.strArea}</h4>
                <h4>Category: {meal.strCategory}</h4>
                <h4>Ingredients:</h4>
                <ul>
                    {currIngred.map((ingred, i)=>
                    <div key={i}>
                        <li>{ingred}</li>
                    </div>)}
                </ul>
            </div>
        </div>
    )
}

export default Categories;
