import { useEffect, useState } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";
import style from '../views/style.css';


const Categories = () => {
    const [categories, setCategories] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [currCat, setCurrCat] = useState([]);

    useEffect(() => {
        axios.get('https://www.themealdb.com/api/json/v1/1/categories.php')
            .then(res=> { 
                setCategories(res.data.categories)
                setLoaded(true);
            })
            .catch(err => console.error(err))
    }, []);

    const currentCat = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${value}`)
            .then(res=> {
                setCurrCat(res.data.meals)
                console.log(res.data)
            })
            .catch(err=>console.error(err))
    }

    return (
        <div>
            {loaded && categories.map((cat, i)=>
            <div key={i}>
                    <div className="flex">
                        <img src={cat.strCategoryThumb} id="menupic" alt="category"/>
                        <button onClick={(e)=>{currentCat(cat.strCategory)}}>
                        <p>{cat.strCategory}</p>
                        </button>
                    </div>
            </div>)}
            <div>
                {/* { currCat ?
                currCat.map((rec, i) => {
                    return <div key={i}>
                    {rec.strMeal}
                </div> }) : <></>
                } */}
            </div>
        </div>
    )
}

export default Categories;
