import { useEffect, useState } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";
import style from '../views/style.css';
import { RiMenuLine } from "react-icons/ri";


const Categories = (props) => {
    const [meal, setMeal] = useState([]);
    const [finish, setFinish] = useState(false);
    const [currIngred, setCurrIngred] = useState([]);
    const [currAmt, setCurrAmt] = useState([]);



    const currMeal = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${value}`)
            .then(res=> {
                setMeal(res.data.meals[0])
                setFinish(true);
                let ingredients = []
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let single = "strIngredient" + i;
                        if(key === single){
                            if(value !== '' && value !== null){
                                ingredients.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrIngred(ingredients);
                let measure = [];
                for(let i=1; i<=20; i++){
                    for(const [key, value] of Object.entries(meal)){
                        let singleVal = "strMeasure" + i;
                        if(key === singleVal){
                            if(value !== '' && value !== null){
                                measure.push(`${value}`)
                            }
                        }
                    }
                }
                setCurrAmt(measure);
            })
            .catch(err=>console.error(err))
    }

    return (
        <div className="colored">
            <div className="centered background total-padding flex">
                <div className="centered single padding">
                    <h2 className="center cat-title">{props.currCat.toUpperCase()}</h2>
                    <div className="flex recipes">
                        {props.ready && props.catMeals ?
                        props.catMeals.map((rec, i) => {
                            return <div key={i} className="scroll">
                                <div className="center">
                                    <img src={rec.strMealThumb} id="subpic"/>
                                </div>
                                <div className="recipe-title center">
                                    <Link className="rec-link" id="rec-title" onClick={(e)=>{currMeal(rec.idMeal)}}>
                                        {rec.strMeal.toUpperCase()}
                                    </Link>
                                </div>
                        </div> }) : <></>
                        }
                    </div>
                </div>
                <div className="marg-left-10 marg-right-10"></div>
                <div className="centered recipe single">
                    {finish && meal ? 
                    <div className="centered padding-10">
                        <h2 className="center marg-top-0 caps">{meal.strMeal.toUpperCase()}</h2>
                        <p className="marg-top-0 center body">{meal.strArea}</p>
                        <div className="flex marg-left-10 marg-right-10 body">
                            <div className="centered single">
                                <img src={meal.strMealThumb} id="recipepic"/>
                            </div>
                            <div className="centered">
                                Ingredients:
                                <div className="flex">
                                    <div>
                                        {currIngred.map((ingred, i)=>
                                        <div key={i}>
                                            <li className="line-spc">{ingred}</li>
                                        </div>)}
                                    </div>
                                    <div>
                                        {currAmt.map((amt, i)=>
                                        <div key={i} className="marg-left-10 line-spc">
                                            {amt}
                                        </div>)}
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="body padding line-spc">
                            <p>Instructions: <br />
                            {meal.strInstructions}</p>
                        </div>
                    </div>: <></> }
                </div>
            </div>
            <br />
            <br />
            <br />
        </div>
    )
}

export default Categories;
