import style from '../views/style.css'
import axios from 'axios'
import { useState } from 'react';
import { Link } from 'react-router-dom';

const LandingPage = (props) => {
    const [currCat, setCurrCat] = useState("");
    const [catMeals, setCatMeals] = useState([]);
    const [ready, setReady] = useState(false);
    
    const currentCat = (value) => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${value}`)
            .then(res=> {
                setCurrCat(value)
                setCatMeals(res.data.meals)
                setReady(true);
                console.log(currCat)
            })
            .catch(err=>console.error(err))
    }

    return (
        <div className='colored'>
            <div className='flex just-cent center'>
                <div className='marg-right-20 border padding'>
                    <h3>CHOOSE BY CATEGORY</h3>
                    <div class="dropdown">
                        <button class="dropbtn">Dropdown</button>
                        <div class="dropdown-content">
                            {props.categories.map((cat, i)=>
                            <span key={i}>
                                <Link onClick={(e)=>{currentCat(cat.strCategory)}}>{cat.strCategory.toUpperCase()}</Link>
                            </span>
                            )}
                        </div>
                    </div>
                </div>
                <div>
                    <br />
                    <h3>OR</h3>
                </div>
                <div className='marg-left-20 border padding'>
                    <h3>LET US PICK FOR YOU!</h3>
                    <button>SURPRISE ME!</button>
                </div>
            </div>
        </div>
    )
}

export default LandingPage;
